create table City(
num number(3),
cityname varchar2(30) PRIMARY KEY,
cityinfo varchar2(30) not null,
continent varchar2(30) not null,
longitude varchar2(10) not null,
latitude varchar2(10) not null,
info varchar2(700) not null,
volt varchar2(20) not null,
hour varchar2(20) not null,
timedifference varchar2(20) not null,
btn varchar2(10) not null
);

select * from City where continent like '%';
update City set cityname = 'SanDiego' where cityname = 'San Diego';
select * from City where cityname like 'Tokyo';

drop table City;
select * from City;

